import React, { Fragment } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { logout, getCategory, clearCategory } from "../actions";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({
  openOrClosed,
  authUser,
  logout,
  getCategory,
  clearCategory,
}) => {
  const { isAuthenticated, user } = authUser;

  const onLogout = () => {
    logout();
  };

  const onClick = (e) => {
    getCategory(e.target.getAttribute("value"));
  };

  const guestLinks = () => {
    return (
      <div className='slide-menu__user-login'>
        <Link to='/account/login'>
          <span>Login</span>
        </Link>
        <Link to='/account/sign-up'>
          <span>Sign Up</span>
        </Link>
      </div>
    );
  };

  const authLinks = () => {
    return (
      <div className='slide-menu__user-login'>
        <span className='slide-menu__user'>
          <i class='lni lni-user'></i>
          <h1>Hello {user && user.name}</h1>
        </span>
        <a className='slide-menu__logout' onClick={onLogout} href='#!'>
          <svg
            xmlns='http://www.w3.org/2000/svg'
            className='h-6 w-6'
            fill='none'
            viewBox='0 0 24 24'
            stroke='currentColor'>
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              strokeWidth={2}
              d='M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1'
            />
          </svg>
          <span>Logout</span>
        </a>
      </div>
    );
  };

  const renderLinks = () => {
    return <Fragment>{isAuthenticated ? authLinks() : guestLinks()}</Fragment>;
  };

  const renderItems = () => {
    return (
      <div className='container-flex flex-dc'>
        <Link to='/'>
          <span>
            <i className='lni lni-home slide-menu__icon'></i>Home
          </span>
        </Link>
        <Link to='/cart'>
          <span>
            <i className='lni lni-cart slide-menu__icon'></i>Cart
          </span>
        </Link>
        <h1>Categories</h1>
        <Link to='/'>
          <span onClick={clearCategory}>All</span>
        </Link>
        <Link to='/'>
          <span onClick={onClick} value="men's clothing">
            Mens Clothing
          </span>
        </Link>
        <Link to='/'>
          <span onClick={onClick} value="women's clothing">
            Womens Clothing
          </span>
        </Link>
        <Link to='/'>
          <span onClick={onClick} value='jewelery'>
            Jewelry
          </span>
        </Link>
        <Link to='/'>
          <span onClick={onClick} value='electronics'>
            Electronics
          </span>
        </Link>
      </div>
    );
  };

  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle}`}>
      {renderLinks()}
      {renderItems()}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
    authUser: state.authUser,
  };
};

export default connect(mapStateToProps, {
  logout,
  getCategory,
  clearCategory,
})(SlideMenu);
