import React, { Fragment } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({ openOrClosed, authUser }) => {
  const { isAuthenticated, user } = authUser;

  const guestLinks = () => {
    return (
      <Fragment>
        <Link to='/account/login'>
          <button className='btn btn--primary'>Login</button>
        </Link>
        <Link to='/account/sign-up'>
          <button className='btn btn--secondary'>Sign Up</button>
        </Link>
      </Fragment>
    );
  };

  const authLinks = () => {
    return (
      <div className='navbar__user-login'>
        <h1>Hello {user && user.name}</h1>
        <a onClick={onLogout} href='#!'>
          <svg
            xmlns='http://www.w3.org/2000/svg'
            className='h-6 w-6'
            fill='none'
            viewBox='0 0 24 24'
            stroke='currentColor'>
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              strokeWidth={2}
              d='M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1'
            />
          </svg>
          <span>Logout</span>
        </a>
      </div>
    );
  };

  const renderInput = () => {
    return <Fragment>{isAuthenticated ? authLinks() : guestLinks()}</Fragment>;
  };

  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle} container-flex flex-dc`}>{renderInput()}</div>
  );
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
    authUser: state.authUser,
  };
};

export default connect(mapStateToProps)(SlideMenu);
