import { CART_ITEM, REMOVE_ITEM } from "../types";

const cartItemReducer = (state = [], action) => {
  switch (action.type) {
    case CART_ITEM:
      return [...state, action.payload];
    case REMOVE_ITEM:
      return [...state];
    default:
      return state;
  }
};

export default cartItemReducer;
