import { CART_ITEM, CART_ITEM_CHECK } from "../types";

const cartItemReducer = (state = [{ image: "/images/chair.jpg" }], action) => {
  switch (action.type) {
    case CART_ITEM:
      return [...state, action.payload];
    case CART_ITEM_CHECK:
      return state;
    default:
      return state;
  }
};

export default cartItemReducer;
