import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { getSingleProduct } from "../actions";
import { Field, reduxForm } from "redux-form";

import css from "../css/product.css";

const Product = (props) => {
  const { singleProduct } = props;
  const { id } = props.match.params;

  const [number, setNumber] = useState(1);

  useEffect(() => {
    props.getSingleProduct(id);
    // eslint-disable-next-line
  }, []);

  if (!singleProduct) {
    return <div>Loading...</div>;
  }

  const onSubmit = (e) => {
    e.preventDefault();
  };

  const onChange = (e) => {
    console.log(formProps);
    setNumber(parseInt(e.target.value));
  };

  const increaseValue = () => {
    setNumber(number + 1);
  };

  const decreaseValue = () => {
    if (number === 0) {
      setNumber(0);
    } else {
      setNumber(number - 1);
    }
  };

  const renderInput = () => {
    return (
      <input
        className='card-input'
        min='1'
        type='number'
        onChange={onChange}
        value={number}
      />
    );
  };

  const renderProduct = () => {
    const { image, title, price, description } = singleProduct;

    return (
      <div className='card mb-3' style={{ maxWidth: "540px" }}>
        <div className='row g-0'>
          <div className='col-md-4'>
            <img src={image} alt={image} />
          </div>
          <div className='col-md-8'>
            <div className='card-body'>
              <h5 className='card-title'>{title}</h5>
              <p className='card-text'>{description}</p>
              <p className='card-text'>
                <small className='text-muted'>${price}</small>
              </p>
              <form onSubmit={onSubmit}>
                <div className='btn-group'>
                  <button className='btn btn-success'>Buy Now</button>
                  <button className='btn btn-info'>Add to cart</button>
                </div>
                <div>
                  <Field component={renderInput} name='product' />
                  <button
                    onClick={() => increaseValue()}
                    className='btn-increase'>
                    +
                  </button>
                  <button
                    onClick={() => decreaseValue()}
                    className='btn-decrease'>
                    -
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return <div className='container'>{renderProduct()}</div>;
};

// const validate = (formProps) => {
//   const errors = {};
//   return errors;
// };

const mapStateToProps = (state) => {
  return {
    singleProduct: state.singleProduct,
  };
};

const product = connect(mapStateToProps, {
  getSingleProduct,
})(Product);

export default reduxForm({
  form: "product",
  // validate: validate,
})(product);
